$(document).ready(function(){{let e=$(".faq__item");$.each(e,function(){let e=$(this).find(".faq__question"),i=$(this).find(".faq__response"),s=$(this).find(".faq__expand");e.on("click",function(){$(".faq__response").slideUp(200),$(".faq__expand").removeClass("active"),i.is(":visible")?(i.slideUp(200),s.removeClass("active")):(i.slideDown(200),s.addClass("active"))})}),$(".faq__question").eq(0).trigger("click")}});